        <div class="login-form-area">
            <div class="login-card">
                <h2>LOGIN</h2>
                <p class="instruction">Masukkan email dan password untuk melanjutkan</p>
                
                <form action="index.php?menu=login" method="post">
                    <div class="form-group">
                        <label for="email">Email atau NIM</label>
                        <input type="text" id="email" name="email" placeholder="Masukkan email atau NIM" required>
                    </div>
                    
                    <div class="form-group">
                        <label for="password">Password</label>
                        <input type="password" id="password" name="password" placeholder="Masukkan password" required>
                    </div>
                    
                    <div class="checkbox-group">
                        <input type="checkbox" id="remember" name="remember">
                        <label for="remember">Ingat saya</label>
                    </div>
                    
                    <button type="submit" class="btn-login">Masuk</button>
                    
                    <a href="registerdanreset/navigator.php?menu=reset" class="forgot-password">Lupa password?</a>
                </form>
            </div>
            
            <p class="register-text">Belum punya akun? <a href="registerdanreset/navigator.php">Daftar sekarang!</a></p>
        </div>
